import { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import { gql, useLazyQuery } from "@apollo/client";
import { motion, AnimatePresence } from "framer-motion";


import Loading from "../../components/global/Loading";
import { Container } from "../../components/layout/Container";

const DEALER_QUERY = gql`
  query DEALER_QUERY($search: String) {
    allDealers(where: { city_contains: $search }) {
      postal
      city
      venue{
        formattedAddress
        googlePlaceID
        lat
        lng
      }
    }
  }
`;

const containerStyle = {
  width: '400px',
  height: '400px'
};

const center = {
  lat: -3.745,
  lng: -38.523
};

import _ from 'lodash';
import { compose, withProps, lifecycle } from"recompose";
import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  Marker,
} from"react-google-maps";
import SearchBox from"react-google-maps/lib/components/places/SearchBox";

const DealerFinder = (props) => {
  const [getDealer, { data, loading, error}] = useLazyQuery(DEALER_QUERY)
  const [locations, setLocations] = useState([])
  const [searchWord, setSearchWord] = useState("")
  //select Input
  const input = document.getElementById('pac-input');
  //initilize searchbox
  let searchBox

  useEffect(() => {
    //call db
    getDealer()
    //load searchbox from google
  }, [])

  useEffect(() => {
    //update state with new db after search
    setLocations(data)
  }, [searchWord])

  // search the word in db
  const handleRefetch = () => {
    getDealer({variables: { search: searchWord }})
  }



  if (loading) return <Loading />;
  if (error) return `Error! ${error}`;


  const handleChange = (e) => {
    setSearchWord(e.target.value)
  }


  const MapWithASearchBox = compose(
    withProps({
      googleMapURL: `https://maps.googleapis.com/maps/api/js?key=${process.env.NEXT_PUBLIC_GOOGLE_MAP_API}&v=3.exp&libraries=geometry,drawing,places`,
      loadingElement: <div style={{ height: `100%` }} />,
      containerElement: <div style={{ height: `400px` }} />,
      mapElement: <div style={{ height: `100%` }} />,
    }),
    withScriptjs,
    withGoogleMap
  )(props =>
    <GoogleMap
      ref={props.onMapMounted}
      defaultZoom={15}
      center={props.center}
      onBoundsChanged={props.onBoundsChanged}
    >
      <SearchBox
        ref={props.onSearchBoxMounted}
        bounds={props.bounds}
        onPlacesChanged={props.onPlacesChanged}
      >
        <input
          type="text"
          placeholder="Customized your placeholder"
          style={{
            boxSizing: `border-box`,
            border: `1px solid transparent`,
            width: `240px`,
            height: `32px`,
            marginTop: `27px`,
            padding: `0 12px`,
            borderRadius: `3px`,
            boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
            fontSize: `14px`,
            outline: `none`,
            textOverflow: `ellipses`,
          }}
        />
      </SearchBox>
      {props.markers.map((marker, index) =>
        <Marker key={index} position={marker.position} />
      )}
    </GoogleMap>
  );


  return(
    <Container space padding>
      <div>
        <h1>test</h1>
        <button onClick={handleRefetch }>Button</button>
        <MapWithASearchBox />
      </div>
    </Container>
  )
}

