import React from "react";
import styled from "styled-components";
import Image from "next/image";
import { Container } from "../layout/Container";

export const FullImage = ({ imageSrc, height = "100%" }) => {
  return (
    <Container>
      <ImageContainer height={height}>
        <Image src={imageSrc} layout="fill" objectFit="cover" />
      </ImageContainer>
    </Container>
  );
};

const AnotherContainer = styled.div`
  display: grid;
  padding: calc(${({ theme }) => theme.paddingW});
`;

const ImageContainer = styled.div`
  display: grid;
  position: relative;
  height: ${(props) => props.height};
  margin: calc(${({ theme }) => theme.paddingW} / 2);
  padding: 0 0 calc(${({ theme }) => theme.paddingW});
  min-height: 300px;
`;
