import Image from "next/image";
import styled from "styled-components";

import Home1 from "../../../assets/images/homepage1.jpg";
import Home2 from "../../../assets/images/homepage2.jpg";
import Home3 from "../../../assets/images/homepage3.jpg";

const TextWelcome = (props) => {
  return (
    <Container>
      <H1Big>
        Frank is a people person, and a real window and door guy. With
        <Inwrap>
          over four decades of
          <ImageS>
            <Image
              src={Home1}
              layout="fill"
              objectFit="cover"
              objectPosition="50% 50%"
            />
          </ImageS>
        </Inwrap>
        experience, Frank’s success is
        <Inwrap>
          <ImageS>
            <Image
              src={Home2}
              layout="fill"
              objectFit="cover"
              objectPosition="50% 50%"
            />
          </ImageS>
          all about strong industry
        </Inwrap>
        partnerships, loyal customers, and longstanding employees.
        <Inwrap>
          Frank is like family.
          <ImageS>
            <Image
              src={Home3}
              layout="fill"
              objectFit="cover"
              objectPosition="50% 50%"
            />
          </ImageS>
        </Inwrap>
      </H1Big>

      <H1Small>
        Frank is a people person, and a real window and door guy. With over four
        decades of experience, Frank’s success is all about strong industry
        partnerships, loyal customers, and longstanding employees. Frank is like
        family.
      </H1Small>
    </Container>
  );
};

const Container = styled.div``;

const Inwrap = styled.div`
  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    display: grid;
    grid-auto-flow: column;
    align-items: center;
    gap: calc(${({ theme }) => theme.gap} / 2);
  }
`;

const H1 = styled.h1`
  display: grid;
  font-size: 8vw;
  line-height: 15vw;
  margin: 0;
  /* white-space: nowrap; */
  @media screen and (min-width: ${({ theme }) => theme.mq.small}) {
    font-size: 5.9vw;
    line-height: 10vw;
  }

  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    font-size: 4vw;
    line-height: 8vw;
  }

  @media screen and (min-width: ${({ theme }) => theme.mq.desktop}) {
    line-height: 7vw;
  }

  @media screen and (min-width: ${({ theme }) => theme.mq.xl}) {
    font-size: 4rem;
    line-height: 7rem;
  }
`;

const H1Small = styled(H1)`
  display: grid;
  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    display: none;
  }
`;

const H1Big = styled(H1)`
  display: none;
  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    display: grid;
  }
`;

const ImageS = styled.div`
  display: none;
  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    display: grid;
    height: 6vw;
    width: 100%;
    min-width: 60px;
    position: relative;
  }
`;

export default TextWelcome;
