import styled from "styled-components";
import Image from "next/image";
import { TertiaryButton } from "../UI/Links";
import Body from "../layout/Body";

import homepage1 from "../../assets/images/homepage1.jpg";

export const DesignOptions = ({ imageSrc }) => {
  const image = imageSrc || homepage1;
  return (
    <Container simple>
      <ImageContainer>
        <Image src={image} objectFit="cover" layout="fill" />
      </ImageContainer>
      <Text simple>
      <Title>Design Options</Title>
        <Body>
          <p>Rated Most efficient by Energy Star</p>

          <p>
          Deciding on the product that’s right for your project is just the beginning. With an extensive array of design options to choose from, you can customize your windows and doors to reflect your home’s architecture, character, and personality.
          </p>
        </Body>
        <TertiaryButton href="/sub/designOptions">
          More on Energy Efficiency
        </TertiaryButton>
      </Text>
    </Container>
  );
};

export const DesignOptionsProduct = ({
  imageSrc1,
  imageSrc2,
  imageSrc3,
}) => {
  return (
    <Container>
      <ImageContainer1>
        <Image src={imageSrc1} objectFit="cover" layout="fill" />
      </ImageContainer1>
      <Text>
        <Title>Design Options</Title>
        <Body>
          <p>Rated Most efficient by Energy Star</p>

          <p>
          Deciding on the product that’s right for your project is just the beginning. With an extensive array of design options to choose from, you can customize your windows and doors to reflect your home’s architecture, character, and personality.
          </p>
        </Body>
        <TertiaryButton href="/sub/designOptions">
        View Design Options
        </TertiaryButton>
      </Text>
      <ImageContainer2>
        <Image src={imageSrc2} objectFit="cover" layout="fill" />
      </ImageContainer2>
      <ImageContainer3>
        <Image src={imageSrc3} objectFit="cover" layout="fill" />
      </ImageContainer3>
    </Container>
  );
};

const Container = styled.div`
  display: grid;
  grid-template-columns: ${(props) => (props.simple ? "inherit" : "1fr 1fr")};
  grid-auto-flow: ${(props) => (props.simple ? "rows" : "columns")};
  grid-template-rows: min-content;
  gap: calc(${({ theme }) => theme.gap} / 2);
`;

const ImageContainer = styled.div`
  display: grid;
  position: relative;
  height: 400px;
`;

const ImageContainer1 = styled(ImageContainer)`
  grid-row: span 2;
  height: auto;
`;

const ImageContainer2 = styled(ImageContainer)``;

const ImageContainer3 = styled(ImageContainer)`
  grid-column: span 2;
`;

const Text = styled.div`
  display: grid;
  gap: ${({ theme }) => theme.gap};
  padding: 2rem;
  color: ${(props) =>
    props.simple ? props.theme.color.secondary : props.theme.color.primary};
`;

const Title = styled.h3`
  display: grid;
`;
