import styled from "styled-components";

export const Container = ({ children, noSpace = false }) => {
  return <ContainerS noSpace={noSpace}>{children}</ContainerS>;
};

const ContainerS = styled.div`
  display: grid;
  max-width: 1440px;
  width: 100%;
  box-sizing: border-box;
  padding: ${(props) => (props.noSpace ? `0rem 0` : `10rem 0 0`)};
  justify-self: center;
  margin: auto;
`;
