import styled from "styled-components";

const Container = ({ children, noSpace }) => {
  return <ContainerS noSpace={noSpace}>{children}</ContainerS>;
};

const ContainerS = styled.div`
  display: grid;
  max-width: 1440px;
  padding: ${({ props }) =>
    props.noSpace
      ? `0rem ${props.theme.paddingW}`
      : `10rem ${props.theme.paddingW}`};
  justify-self: center;
  margin: auto;
`;

export default Container;
