import React from "react";
import styled from "styled-components";

import { Container } from "../../components/layout/Container";
import Split from "../../components/pageSpecific/products/Split";

import image1 from "../../assets/images/product/image1.jpg";
import image2 from "../../assets/images/product/image2.jpg";
import image3 from "../../assets/images/product/image3.jpg";
import image4 from "../../assets/images/product/image4.jpg";
import { PageTitle } from "../../components/pageSpecific/products/PageTitle";

const entrydoors = () => {
  const pagetitle = "Entry Doors";
  const links = [
    {
      name: "Patio Doors",
      href: "/products/patiodoors",
    },
    {
      name: "Windows",
      href: "/products/windows",
    },
    {
      name: "Design Options",
      href: "/products/designoptions",
    },
  ];

  return (
    <Container>
      <Context>
        <PageTitle title={pagetitle} links={links} />
        <Split
          title="Modo"
          subtitle="Smooth fibreglass doors 
          available in a range of options."
          href="/products/entrydoors/modo"
          image={image1}
        >
          <p>
            For a simple replacement or add some style to your front facade.
          </p>

          <p>
            Expansive selection of glass configurations in flush glazed and
            modern door lite designs.
          </p>

          <p>
            Choose to add a punch of colour, and make this a showstopper
            entrance way.
          </p>
        </Split>
        <Split
          title="Classic"
          subtitle="Mahogany and Oak grain 
          in all the traditional styles."
          href="/products/entrydoors/classic"
          image={image2}
          side
        >
          <p>
            The industry leading fibreglass door with ultra realistic wood grain
            technology.
          </p>

          <p>
            Expansive selection of glass configurations in flush glazed and
            modern door lite designs.
          </p>

          <p>The look and feel of a real wood door with all the upside.</p>
        </Split>
        <Split
          title="Artisan"
          subtitle="Mixing Old World values with 
          New World technology and design."
          href="/products/entrydoors/artisan"
          image={image3}
        >
          <p>
            Available in a range of styles and finishes that fool the eye --
            Mahogany, Oak, Fir, Rustic, and Canvas.
          </p>

          <p>
            Superior quality construction and detailed craftsmanship that allows
            for 10% more glass than other entry door lines, and a clean hard
            edge that further mimics real wood.
          </p>

          <p>The handcrafted elegance of another era for today’s homeowner.</p>
        </Split>
      </Context>
    </Container>
  );
};

const Context = styled.div`
  display: grid;
  gap: 4rem;
`;

export default entrydoors;
