import React from "react";
import styled from "styled-components";
import Image from "next/image";
import { TertiaryButton } from "../../UI/Links";

const Category = ({ image, title, subtitle, children, href, rev = false }) => {
  return (
    <Container rev={rev}>
      <Left rev={rev}>
        <Image src={image} objectFit="cover" layout="fill" />
      </Left>
      <Right>
        <Title>{title}</Title>
        <Subtitle>{subtitle}</Subtitle>
        <Body>{children}</Body>
        <TertiaryButton href={href}>View {title}</TertiaryButton>
      </Right>
    </Container>
  );
};

const Container = styled.div`
  display: grid;
  grid-template-columns: ${(props) => (props.rev ? "2fr 3fr" : "3fr 2fr")};
  margin-top: ${({ theme }) => `-${theme.paddingW}`};
`;

const Left = styled.div`
  display: grid;
  position: relative;
  height: 100%;
  order: ${(props) => (props.rev ? "1" : "0")};
`;

const Right = styled.div`
  display: grid;
  padding: ${({ theme }) =>
    `calc(3 * ${theme.paddingW}) ${theme.paddingW} calc(6 * ${theme.paddingW})`};
  align-content: start;
  gap: 1.5rem;
`;

const Title = styled.h3`
  display: grid;
`;

const Subtitle = styled.h6`
  display: grid;
`;

const Body = styled.div`
  display: grid;
  gap: 0.5rem;
`;

export default Category;
