import { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import { gql, useQuery } from "@apollo/client";
import { motion, AnimatePresence } from "framer-motion";
import { GoogleMap, useLoadScript } from '@react-google-maps/api';

import Loading from "../../components/global/Loading";
import { Container } from "../../components/layout/Container";

const DEALER_QUERY = gql`
  query DEALER_QUERY($search: String) {
    allDealers(where: { OR: [{city_contains_i: $search},{name_contains_i:$search},{postal_contains_i:$search}] }) {
      postal
      city
      name
      province
      id
      venue{
        formattedAddress
        googlePlaceID
        lat
        lng
      }
    }
  }
`;

const containerStyle = {
  width: '100%',
  height: '400px'
};



const DealerFinder = (props) => {
  const [searchWord, setSearchWord] = useState("")
  const { data, loading, error, refetch} = useQuery(DEALER_QUERY,{
    fetchPolicy: "cache-and-network",
    variables: { search: searchWord }
  })
  const [locations, setLocations] = useState([])
  const [location, setLocation] = useState({lat: -3.745,
    lng: -38.523})

  console.log(data?.allDealers, locations);


  useEffect(() => {
    //call db
    !loading && setLocations(data?.allDealers)
  }, [loading])

  useEffect(() => {
    //update state with new db after search
    !loading && data?.allDealers.length > 0 && setLocations(data?.allDealers)
  },[searchWord])

  //load google map and initilize
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.NEXT_PUBLIC_GOOGLE_MAP_API,
  })

  if (loading) return <Loading />;
  if (error) return `Error! ${error}`;

  if (loadError) {
    return <div>Map cannot be loaded right now, sorry.</div>
  }

  const handleChange = (e) => {
    e.preventDefault();
    setSearchWord(e.target.value)
  }

  return(
    <Container space padding>
      <Input  id="search " name="q" onChange={handleChange} value={searchWord}/>
      <Content>
      <Left>
        {/* {(locations.length !== 0 ) ?  
          locations.map(item => {
            return (
            <Dealer key={item.id}>
              <h6>{item.name}</h6>
              <p>{item.venue.formattedAddress}</p>
            </Dealer>
            )
          })
        :(<p>No Location Found</p>)} */}
      </Left>
      <Right>
        <Fixed>
          <p>Ostaco Windows and Doors products are proudly distributed by many retailers across Ontario. Please fill out this form and our team will quickly get back to you with information on your nearest dealer.</p>

            {/* {isLoaded ? (
              <>

                <GoogleMap
                mapContainerStyle={containerStyle}
                center={location}
                zoom={10}
                >
                </GoogleMap>
              </>
            ) : <Loading /> 
            } */}
        </Fixed>
      </Right>
      </Content>
    </Container>
  )
}


// const Container = styled.div`
//   display: grid;
//   grid-gap: 1.5em;
//   @media (min-width: 768px) {
//     grid-template-columns: 2fr 3fr;
//   }
// `;

// const LoadingS = custom(styled(Loading)``)

const Content = styled.div`
  display: grid;
  grid-template-columns: 1fr 3fr;
  gap: 3rem;
`;

const Input = styled.input`
  display: grid;
`;


const Left = styled.ul`
  display: grid;
  gap: 3rem;
`;

const Right = styled.div`
  display: grid;
  @media (min-width: 768px) {
    position: relative;
  }
`;

const H2 = styled.h2`
  font-size: 0.875rem;
  font-weight: 400;
  max-width: 450px;
`;

const Fixed = styled.div`
  display: grid;
  grid-gap: 2vh;
  @media (min-width: 768px) {
    position: fixed;
    width: 52.5vw;
  }
`;

const InputS = styled.input`
  max-width: 450px;
`;

// const Content = styled.ul`
//   list-style: none;
//   padding-inline-start: 0px;
//   display: grid;
//   grid-gap: 1.5em;
//   h4 {
//     margin: 16px 0 0;
//   }
// `;

const Dealer = styled.li`
  display: grid;
  grid-gap: 0.5em;
  h6 {
    margin: 0;
  }
`;

const InRow = styled.div`
  display: grid;
  grid-auto-flow: column;
  justify-content: start;
  align-items: baseline;
  grid-gap: 1em;
`;

const City = styled.div`
  h4 {
    grid-column: 1/-1;
  }
  display: grid;
  grid-gap: 1em;
`;

const Form = styled.form`
  display: grid;
`;

export default DealerFinder;
