import React from "react";
import styled from "styled-components";
import Image from "next/image";
import PropTypes from "prop-types";

import { TertiaryButton } from "../UI/Links";

const Split = ({ title, subtitle, href, button, image, children, side }) => {
  return (
    <Container side={side}>
      <Text>
        <Title>{title}</Title>
        <Subtitle>{subtitle}</Subtitle>
        <div>{children}</div>
        <TertiaryButton rev={side ? false : true} href={href}>
          {button}
        </TertiaryButton>
      </Text>
      <ImageContainer>
        <Image
          src={image}
          layout="fill"
          objectFit="cover"
          objectPosition="50% 50%"
        />
      </ImageContainer>
    </Container>
  );
};

const ImageContainer = styled.div`
  display: grid;
  position: relative;
  height: 100%;
  width: 100%;
`;

const Text = styled.div`
  display: grid;
  gap: 1rem;
  padding: 2rem 1rem;
  align-items: center;
  align-content: center;
`;

const Title = styled.h2`
  display: grid;
`;

const Subtitle = styled.h4`
  display: grid;
`;

const Container = styled.div`
  display: grid;
  grid-template-columns: ${(props) => (props.side ? "60% 40%" : "40% 60%")};
  gap: 4rem;
  box-sizing: border-box;
  height: ${(props) => (props.side ? "50rem" : "31rem")};
  grid-auto-flow: ${(props) => (props.side ? "column dense" : "inherit")};
  color: ${(props) =>
    props.side ? props.theme.color.primary : props.theme.color.white};
`;

Split.defaultProps = {
  button: "View Product",
};

Split.propTypes = {
  title: PropTypes.string.isRequired,
  subtitle: PropTypes.string,
  href: PropTypes.string,
  button: PropTypes.string,
  image: PropTypes.object,
  side: PropTypes.bool,
};

export default Split;
