import styled from "styled-components";

export const Container = ({ children, space = false, gap = false }) => {
  return (
    <ContainerS space={space} gap={gap}>
      {children}
    </ContainerS>
  );
};

const ContainerS = styled.div`
  display: grid;
  max-width: 1440px;
  width: 100%;
  box-sizing: border-box;
  padding: ${(props) => (props.space ? `10rem 0` : `0`)};
  justify-self: center;
  margin: auto;
  gap: ${(props) => (props.gap ? props.theme.paddingW : `0`)};
`;
