import Image from "next/image";
import styled from "styled-components";

import Home1 from "../../../assets/images/homepage1.jpg";
import Home2 from "../../../assets/images/homepage2.jpg";
import Home3 from "../../../assets/images/homepage3.jpg";

const TextWelcome = (props) => {
  return (
    <Container>
      <H1>
        <InRow>Frank is a people person, and a</InRow>
        <InRow>real window and door guy. With</InRow>
        <InRow>
          over four decades of{" "}
          <Image src={Home1} layout="fill" width={300} height={300} />
        </InRow>
        <InRow>experience, Frankâ€™s success is</InRow>
        <InRow>
          <Image src={Home2} layout="fill" width={300} height={300} /> all about
          strong industry
        </InRow>
        <InRow>partnerships, loyal customers,</InRow>
        <InRow>and longstanding employees.</InRow>
        <InRow>
          Frank is like family.{" "}
          <Image src={Home3} layout="fill" width={300} height={300} />
        </InRow>
      </H1>
    </Container>
  );
};

const Container = styled.div``;

const H1 = styled.h1`
  display: grid;
  font-size: 8vw;
  line-height: 15vw;
  margin: 0;
  white-space: nowrap;
  @media (min-width: 480px) {
    font-size: 4vw;
    line-height: 85px;
  }
`;

const InRow = styled.div`
  display: grid;
  grid-auto-flow: column;
  justify-content: start;
  align-items: center;
  grid-gap: 16px;
`;

const HomeImage = styled.div`
  background-image: url(${(props) => props.bg});
  background-size: cover;
  background-position: center center;
  height: 80px;
  width: 20vw;
  margin: 0;
`;

export default TextWelcome;
