import { useFormik, Form, Field, ErrorMessage } from "formik";
import styled from "styled-components";
import { Container } from "../components/layout/Container";
import ContactForm from "../components/pageSpecific/contact/ContactForm";

const list = [
  {
    name:"General Inquiry",
    email:"hello@frankwd.com",
    phone:"905 660 5021"
  },
  {
    name:"Customer Service",
    email:"info@frankwd.com"
  },
  {
    name:"Sales / Dealer Support",
    email:"sales@frankwd.com"
  },
  {
    name:"Professional Support",
    email:"tech@frankwd.com"
  }
]

const contactus = () => {
  return <Container space padding>

    <Content>
    <h1>Contact Us</h1>
      <Left>
        {list.map(item => (
          <Detail>
          <h5>{item.name}</h5>
          <a href={`mailto:${item.email}`}>{item.email}</a>
          {item.phone && <a href={`tel:${item.phone}`}>{item.phone}</a>}
          </Detail>
        ))}

      </Left>
      <Right>
        <ContactForm />
      </Right>
    </Content>
  </Container>;
};

const Content = styled.div`
  display: grid;
  gap: ${({ theme }) => theme.gap};
`;

const Left = styled.div`
  display: grid;
  gap:2rem;
  a{
    font-weight: 400;
  }
  h5{
    font-weight:600;
  }

  @media screen and (min-width: ${({ theme }) => theme.mq.small}){
   position: sticky;
   top: 5rem;   
  }
`;

const Right = styled.div`
  display: grid;
`;

const Detail = styled.div`
  display: grid;
  gap:0.25rem;
`;



export default contactus;
