import React from "react";
import styled from "styled-components";

import { Container } from "../../components/layout/Container";
import Split from "../../components/pageSpecific/products/Split";

import image1 from "../../assets/images/product/image1.jpg";
import image2 from "../../assets/images/product/image2.jpg";
import image3 from "../../assets/images/product/image3.jpg";
import image4 from "../../assets/images/product/image4.jpg";
import { PageTitle } from "../../components/pageSpecific/products/PageTitle";

const windows = () => {
  const pagetitle = "Windows";
  const links = [
    {
      name: "Entry Doors",
      href: "/products/entrydoors",
    },
    {
      name: "Patio Doors",
      href: "/products/patiodoors",
    },
    {
      name: "Design Options",
      href: "/products/designoptions",
    },
  ];

  const products = [
    {
      title: "Core",
      subtitle: "Ideal for renovation or new construction",
      href: "/products/window/core",
      image: image1,
      children: `<p>Rated Most efficient by Energy Star</p>
          <p>
            Warm edge spacers recessed between the panes of glass both minimize
            glass edge conductivity while optimally containing the argon gas
            within the sealed units, obtaining a 90% argon gas fill rate.
          </p>`,
    },
    {
      title: "Core+",
      subtitle: "Classic wood window design meets modern engineering",
      href: "/products/window/coreplus",
      image: image2,
      side: true,
      children: `<p>Rated Most efficient by Energy Star</p>
          <p>
            Warm edge spacers recessed between the panes of glass both minimize
            glass edge conductivity while optimally containing the argon gas
            within the sealed units, obtaining a 90% argon gas fill rate.
          </p>`,
    },
    {
      title: "Enersense",
      subtitle: "Maximize your living space.",
      href: "/products/window/enersense",
      image: image3,
      children: `<p>Rated Most efficient by Energy Star</p>
          <p>
            Warm edge spacers recessed between the panes of glass both minimize
            glass edge conductivity while optimally containing the argon gas
            within the sealed units, obtaining a 90% argon gas fill rate.
          </p>`,
    },
    {
      title: "Prism",
      subtitle: "More glass surface area for more light.",
      href: "/products/window/prism",
      image: image4,

      side: true,
      children: `<p>Rated Most efficient by Energy Star</p>
          <p>
            Warm edge spacers recessed between the panes of glass both minimize
            glass edge conductivity while optimally containing the argon gas
            within the sealed units, obtaining a 90% argon gas fill rate.
          </p>`,
    },
  ];

  return (
    <Container>
      <Context>
        <PageTitle title={pagetitle} links={links} />
        {products.map((product, index) => {
          const {
            title,
            subtitle,
            href,
            image,
            side = false,
            children,
          } = product;
          return (
            <Split
              title={title}
              subtitle={subtitle}
              href={href}
              image={image}
              side={side}
            >
              {children}
            </Split>
          );
        })}
      </Context>
    </Container>
  );
};

const Context = styled.div`
  display: grid;
  gap: 4rem;
`;

export default windows;
