import React from "react";
import styled from "styled-components";
import Image from "next/image";
import PropTypes from "prop-types";

import { TertiaryButton } from "../../UI/Links";

const Split = ({ title, subtitle, href, button, image, children, side }) => {
  return (
    <Container side={side}>
      <Text side={side}>
        <Title>{title}</Title>
        <Subtitle>{subtitle}</Subtitle>
        <div>{children}</div>
        <TertiaryButton rev={side ? false : true} href={href}>
          {button}
        </TertiaryButton>
      </Text>
      <ImageContainer>
        <Image
          src={image}
          layout="fill"
          objectFit="cover"
          objectPosition="50% 50%"
        />
      </ImageContainer>
    </Container>
  );
};

const ImageContainer = styled.div`
  display: grid;
  position: relative;
  height: 100%;
  width: 100%;
`;

const Text = styled.div`
  display: grid;
  gap: 1rem;
  padding: ${({ theme }) => theme.paddingW};
  align-items: center;
  align-content: center;
  order: ${(props) => (props.side ? 1 : 0)};
`;

const Title = styled.h2`
  display: grid;
`;

const Subtitle = styled.h4`
  display: grid;
`;

const Container = styled.div`
  display: grid;

  gap: 2rem;
  box-sizing: border-box;

  color: ${(props) =>
    props.side ? props.theme.color.primary : props.theme.color.white};
  background-color: ${(props) =>
    props.side ? props.theme.color.bg : props.theme.color.primary};

  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    grid-template-columns: ${(props) => (props.side ? "45% 55%" : "55% 45%")};
    min-height: ${(props) => (props.side ? "50rem" : "31rem")};
  }
`;

Split.defaultProps = {
  button: "View Product",
};

Split.propTypes = {
  title: PropTypes.string.isRequired,
  subtitle: PropTypes.string,
  href: PropTypes.string,
  button: PropTypes.string,
  image: PropTypes.object,
  side: PropTypes.bool,
};

export default Split;
