import React from "react";
import styled from "styled-components";
import Image from "next/image";
import Link from "next/link";
import PropTypes from "prop-types";

import { TertiaryButton } from "../../UI/Links";

const Split = ({
  title,
  subtitle,
  href,
  parent,
  button,
  image,
  children,
  side,
}) => {
  return (
    <Link
      href={{
        pathname: href,
        query: { id: parent },
      }}
    >
      <Container side={side}>
        <ImageContainer>
          <Image
            src={image}
            layout="fill"
            objectFit="cover"
            objectPosition="50% 50%"
          />
        </ImageContainer>
        <Text side={side}>
          <Title>{title}</Title>
          <Subtitle>{subtitle}</Subtitle>
          <div>{children}</div>
          <TertiaryButton rev={side ? false : true} href={href}>
            {button}
          </TertiaryButton>
        </Text>
      </Container>
    </Link>
  );
};

const ImageContainer = styled.div`
  display: grid;
  position: relative;
  width: 100%;
  height: 300px;
  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    height: 100%;
  }
`;

const Text = styled.div`
  display: grid;
  gap: 1rem;
  padding: ${({ theme }) => theme.paddingW};
  align-items: center;
  align-content: center;

  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    order: ${(props) => (props.side ? 0 : -1)};
  }
`;

const Title = styled.h2`
  display: grid;
`;

const Subtitle = styled.h4`
  display: grid;
  max-width: 35rem;
`;

const Container = styled.div`
  display: grid;

  box-sizing: border-box;

  color: ${(props) =>
    props.side ? props.theme.color.primary : props.theme.color.white};
  background-color: ${(props) =>
    props.side ? props.theme.color.bg : props.theme.color.primary};

  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    grid-template-columns: ${(props) => (props.side ? "45% 55%" : "55% 45%")};
    min-height: ${(props) => (props.side ? "50rem" : "31rem")};
  }
`;

Split.defaultProps = {
  button: "View Product",
};

Split.propTypes = {
  title: PropTypes.string.isRequired,
  subtitle: PropTypes.string,
  href: PropTypes.object,
  button: PropTypes.string,
  image: PropTypes.string,
  side: PropTypes.bool,
};

export default Split;
