import { useState } from "react";
import styled from "styled-components";
import Link from "next/link";
import Image from "next/image";

import MenuImage from "../../assets/images/menuImages/menu.jpg";
import Menu1 from "../../assets/images/menuImages/menu1.jpg";
import Menu2 from "../../assets/images/menuImages/menu2.jpg";
import Menu3 from "../../assets/images/menuImages/menu3.jpg";
import Menu4 from "../../assets/images/menuImages/menu4.jpg";
import Menu5 from "../../assets/images/menuImages/menu5.jpg";
import Menu6 from "../../assets/images/menuImages/menu6.jpg";
import Menu7 from "../../assets/images/menuImages/menu7.jpg";
import Menu8 from "../../assets/images/menuImages/menu8.jpg";
import Menu9 from "../../assets/images/menuImages/menu9.jpg";
import Menu10 from "../../assets/images/menuImages/menu10.jpg";
import Menu11 from "../../assets/images/menuImages/menu11.jpg";
import Menu12 from "../../assets/images/menuImages/menu12.jpg";
import Menu13 from "../../assets/images/menuImages/menu13.jpg";
import Menu14 from "../../assets/images/menuImages/menu14.jpg";
import Menu15 from "../../assets/images/menuImages/menu15.jpg";
import Menu16 from "../../assets/images/menuImages/menu16.jpg";
import Menu17 from "../../assets/images/menuImages/menu17.jpg";
import Menu18 from "../../assets/images/menuImages/menu18.jpg";
import Menu19 from "../../assets/images/menuImages/menu19.jpg";
import Menu20 from "../../assets/images/menuImages/menu20.jpg";

import { motion, AnimatePresence } from "framer-motion";
import { NavLinks } from "../UI/Links";

const Menu = (props) => {
  const [imageSrc, setImageSrc] = useState(MenuImage);

  const opacity = {
    initial: { x: -10, opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: 10, opacity: 0 },
  };

  const links = [
    {
      item: [
        {
          href: "/brochure/windows",
          name: "Windows",
          image: Menu1,
          image2: Menu2,
        },
        {
          href: "/brochure/doors",
          name: "Doors",
          image: Menu3,
          image2: Menu4,
        },
        {
          href: "/brochure/patio",
          name: "Patio Doors",
          image: Menu5,
          image2: Menu6,
        },
      ],
    },
    {
      item: [
        {
          href: "/sub/frank",
          name: "Who is Frank?",
          image: Menu7,
          image2: Menu8,
        },
        {
          href: "/sub/imagine",
          name: "Imagine",
          image: Menu9,
          image2: Menu10,
        },
        {
          href: "/sub/designOptions",
          name: "Design Options",
          image: Menu13,
          image2: Menu14,
        },
        {
          href: "/sub/learn",
          name: "Learn",
          image: Menu13,
          image2: Menu14,
        },
        {
          href: "/sub/make",
          name: "Make",
          image: Menu11,
          image2: Menu12,
        },
        {
          href: "/dealerFinder",
          name: "Find a Dealer",
          image: Menu19,
          image2: Menu20,
        },
      ],
    },
    {
      item: [
        {
          href: "/contactUs",
          name: "Contact Us",
          image: Menu15,
          image2: Menu16,
          prop: "Bottom",
        },
        {
          href: "/user/account",
          name: props.me ? "Account" : "Dealer Login",
          image: Menu17,
          image2: Menu18,
          prop: "Bottom",
        },
      ],
    },
  ];

  const variants = {
    visible: { opacity: 1, transition: { duration: 3 } },
    hidden: { opacity: 0, transition: { duration: 3 } },
  };

  return (
    <AnimatePresence exitBeforeEnter>
      {props.open && (
        <Container
          initial={{ y: "-100vh" }}
          animate={{ y: "0vh" }}
          transition={{ duration: 0.25 }}
          exit={{ y: "-100vh" }}
          key="container"
        >
          <Left
            initial="initial"
            animate="animate"
            exit="exit"
            variants={opacity}
            transition={{ delay: 0.35, duration: 0.75 }}
          >
            <ImgContainer variants={variants}>
              <Image layout="fill" objectFit="cover" src={imageSrc} />
            </ImgContainer>
          </Left>
          <Right
            initial="initial"
            animate="animate"
            exit="exit"
            variants={opacity}
            transition={{ delay: 0.65, duration: 0.75 }}
          >
            {links.map((location) => {
              return (
                <Divide>
                  {location.item.map((it) => (
                    <div onMouseEnter={() => setImageSrc(it.image)}>
                      <NavLinks href={it.href} Bottom={it.prop}>
                        {it.name}
                      </NavLinks>
                    </div>
                  ))}
                </Divide>
              );
            })}
          </Right>
        </Container>
      )}
    </AnimatePresence>
  );
};

const Container = styled(motion.div)`
  display: grid;
  grid-gap: 3.5rem;
  position: absolute;
  background-color: ${({ theme }) => theme.color.bg};

  top: 0;
  padding: 160px 5vw 5vh;
  height: 100vh;
  width: 100vw;
  box-sizing: border-box;

  @media (min-width: 768px) {
    grid-template-columns: auto 25%;
  }
`;

const Left = styled(motion.div)`
  display: none;
  @media (min-width: 768px) {
    display: grid;
  }
`;

const ImgContainer = styled(motion.div)`
  position: relative;
  height: calc(90vh - 160px);
`;

const Right = styled(motion.div)`
  display: grid;
  grid-gap: 3.5rem;
  align-items: start;
  align-content: start;

  @media (min-width: 768px) {
    align-content: flex-start;
    height: calc(90vh - 160px);
  }
`;

const Divide = styled.div`
  display: grid;
  grid-gap: 1rem;
`;

const HR = styled.div`
  width: 100%;
  height: 1px;
  background-color: ${({ theme }) => theme.color.primary};
`;

export default Menu;
