import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import { useRouter } from "next/router";

import { NavLinks } from "../../UI/Links";

export const PageTitle = ({ title, links }) => {
  return (
    <Context>
      <h2>{title}</h2>
      <Links>
        {links?.map((item, index) => (
          <NavLinks key={index} href={item.href}>
            {item.name}
          </NavLinks>
        ))}
      </Links>
    </Context>
  );
};

export const Breadcrumbs = ({ links }) => {
  const router = useRouter();
  console.log(router);

  const { parent, title } = router.query;
  return (
    <Context>
      <TitleBox>
        <h5 onClick={() => router.back()}>{parent}</h5>
        <Line />
        <h5>{title}</h5>
      </TitleBox>

      <Links>
        {links?.map((item) => (
          <NavLinks href={item.href}>{item.name}</NavLinks>
        ))}
      </Links>
    </Context>
  );
};

const TitleBox = styled.div`
  display: grid;
  grid-auto-flow: column;
  align-items: baseline;
  justify-content: start;
  gap: 1rem;
  h5 {
    cursor: pointer;
  }
`;

const Line = styled.div`
  display: grid;
  width: 3rem;
  height: 1px;
  background-color: ${({ theme }) => theme.color.secondary};
`;

const Links = styled.div`
  display: grid;
`;

const Context = styled.div`
  display: grid;
  padding: 0 ${({ theme }) => theme.paddingW};

  @media screen and (min-width: ${({ theme }) => theme.mq.tablet}) {
    grid-template-columns: auto 25%;
    align-items: baseline;
  }
`;

PageTitle.propTypes = {
  title: PropTypes.string,
  links: PropTypes.array,
};
