import styled from "styled-components";
import Link from "next/link";
import MenuImage from "../../assets/images/menu.jpg";
import { motion, AnimatePresence } from "framer-motion";

const Menu = (props) => {
  const opacity = {
    initial: { x: -10, opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: 10, opacity: 0 },
  };

  return (
    <AnimatePresence exitBeforeEnter>
      {props.open && (
        <Container
          initial={{ y: "-100vh" }}
          animate={{ y: "0vh" }}
          transition={{ duration: 0.25 }}
          exit={{ y: "-100vh" }}
          key="container"
        >
          <Left
            initial="initial"
            animate="animate"
            exit="exit"
            variants={opacity}
            transition={{ delay: 0.35, duration: 0.75 }}
          >
            <ImgContainer src={MenuImage} />
          </Left>
          <Right
            initial="initial"
            animate="animate"
            exit="exit"
            variants={opacity}
            transition={{ delay: 0.65, duration: 0.75 }}
          >
            <Top>
              <Link href="/brochure/windows">Windows</Link>
              <Link href="/brochure/doors">Doors</Link>
              <Link href="/brochure/patio">Patio Doors</Link>
            </Top>
            <Mid>
              <Link href="/sub/frank">Who is Frank?</Link>
              <Link href="/sub/imagine">Imagine</Link>
              <Link href="/sub/make">Make</Link>
              <Link href="/sub/learn">Learn</Link>
            </Mid>
            <Bottom>
              <Link href="/contactUs">Contact Us</Link>
              <Link href="/user/account">
                {props.me ? "Account" : "Dealer Login"}
              </Link>
              <Link href="/dealerFinder">Find a Dealer</Link>
            </Bottom>
          </Right>
        </Container>
      )}
    </AnimatePresence>
  );
};

const Container = styled(motion.div)`
  display: grid;
  grid-gap: 3.5rem;
  position: absolute;
  background-color: ${({ theme }) => theme.color.bg};

  top: 0;
  padding: 160px 5vw 5vh;
  height: 100vh;
  width: 100vw;
  box-sizing: border-box;

  @media (min-width: 768px) {
    grid-template-columns: auto 25%;
  }
`;

const Left = styled(motion.div)`
  display: none;
  @media (min-width: 768px) {
    display: grid;
  }
`;

const ImgContainer = styled.div`
  background-image: ${(props) => `url(${props.src})`};
  background-position: center center;
  background-size: cover;
  height: calc(90vh - 160px);
`;
const Right = styled(motion.div)`
  display: grid;
  grid-gap: 3.5rem;
  align-items: start;
  align-content: start;

  @media (min-width: 768px) {
    align-content: space-between;
    height: calc(90vh - 160px);
  }
`;

const Top = styled.div`
  display: grid;
  grid-gap: 1rem;
`;

const Mid = styled.div`
  display: grid;
  grid-gap: 1rem;
`;

const Bottom = styled.div`
  display: grid;
  grid-gap: 1rem;
`;

const HR = styled.div`
  width: 100%;
  height: 1px;
  background-color: ${({ theme }) => theme.color.primary};
`;

export default Menu;
