import React from "react";
import styled from "styled-components";
import Image from "next/image";
import PropTypes from "prop-types";

import { TertiaryButton } from "../UI/Links";

const Split = ({ title, subtitle, href, button, image, children, side }) => {
  return (
    <Container side={side}>
      <Text side={side}>
        <Title>{title}</Title>
        <Subtitle>{subtitle}</Subtitle>
        <div>{children}</div>
        <TertiaryButton rev={side ? false : true} href={href}>
          {button}
        </TertiaryButton>
      </Text>
      <ImageContainer>
        <Image
          src={image}
          layout="fill"
          objectFit="cover"
          objectPosition="50% 50%"
        />
      </ImageContainer>
    </Container>
  );
};

const ImageContainer = styled.div`
  display: grid;
  position: relative;
  height: 100%;
  width: 100%;
`;

const Text = styled.div`
  display: grid;
  gap: 1rem;
  padding: ${({ theme }) => theme.paddingW};
  align-items: center;
  align-content: center;
  order: ${(props) => (props.side ? 1 : 0)};
`;

const Title = styled.h2`
  display: grid;
`;

const Subtitle = styled.h4`
  display: grid;
`;

const Container = styled.div`
  display: grid;
  grid-template-columns: 50% 50%;
  gap: 2rem;
  box-sizing: border-box;
  min-height: ${(props) => (props.side ? "50rem" : "31rem")};

  color: ${(props) =>
    props.side ? props.theme.color.primary : props.theme.color.white};
  background-color: ${(props) =>
    props.side ? props.theme.color.bg : props.theme.color.primary};
`;

Split.defaultProps = {
  button: "View Product",
};

Split.propTypes = {
  title: PropTypes.string.isRequired,
  subtitle: PropTypes.string,
  href: PropTypes.string,
  button: PropTypes.string,
  image: PropTypes.object,
  side: PropTypes.bool,
};

export default Split;
