import { useState, useEffect, useCallback } from "react";
import styled from "styled-components";
import { gql, useLazyQuery } from "@apollo/client";
import { motion, AnimatePresence } from "framer-motion";
import { GoogleMap, useLoadScript } from '@react-google-maps/api';
import { StandaloneSearchBox } from '@react-google-maps/api';

import Loading from "../../components/global/Loading";
import { Container } from "../../components/layout/Container";

const DEALER_QUERY = gql`
  query DEALER_QUERY($search: String) {
    allDealers(where: { city_contains: $search }) {
      postal
      city
      venue{
        formattedAddress
        googlePlaceID
        lat
        lng
      }
    }
  }
`;

const containerStyle = {
  width: '100%',
  height: '400px'
};

const center = {
  lat: -3.745,
  lng: -38.523
};




const DealerFinder = (props) => {
  const [getDealer, { data, loading, error}] = useLazyQuery(DEALER_QUERY)
  const [locations, setLocations] = useState([])
  const [searchWord, setSearchWord] = useState("")
  //select Input
  const input = document.getElementById('pac-input');
  //initilize searchbox
  let searchBox

  useEffect(() => {
    //call db
    !loading && getDealer()
    //load searchbox from google
    if(isLoaded) {
      searchBox = new google.maps.places.SearchBox(input); 
      console.log(searchBox.getPlaces());
    }
  }, [])

  useEffect(() => {
    //update state with new db after search
    setLocations(data)
  }, [searchWord])
console.log(locations);

  // search the word in db
  const handleRefetch = () => {
    getDealer({variables: { search: searchWord }})
  }

  //load google map and initilize
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.NEXT_PUBLIC_GOOGLE_MAP_API,
  })

  if (loading) return <Loading />;
  if (error) return `Error! ${error}`;

  if (loadError) {
    return <div>Map cannot be loaded right now, sorry.</div>
  }

  const handleChange = (e) => {
    setSearchWord(e.target.value)
  }


  return(
    <Container space padding>
      <div>
        <h1>test</h1>
        <button onClick={handleRefetch }>Button</button>
        {isLoaded ? (
          <>
            <p>test</p>
            <GoogleMap
            // options={options}
            // onLoad={onLoad}
            mapContainerStyle={containerStyle}
            center={center}
            zoom={10}
            >

            </GoogleMap>
          </>
        ) : <Loading /> }
      </div>
    </Container>
  )
}


// const Container = styled(motion.div)`
//   display: grid;
//   grid-gap: 1.5em;
//   @media (min-width: 768px) {
//     grid-template-columns: 2fr 3fr;
//   }
// `;

const LoadingS = motion.custom(styled(Loading)``)


const Left = styled(motion.div)`
  display: grid;
  grid-row: 2;
  @media (min-width: 768px) {
    grid-row: 1;
  }
`;

const Right = styled(motion.div)`
  display: grid;
  @media (min-width: 768px) {
    position: relative;
  }
`;

const H2 = styled.h2`
  font-size: 0.875rem;
  font-weight: 400;
  max-width: 450px;
`;

const Fixed = styled.div`
  display: grid;
  grid-gap: 2vh;
  @media (min-width: 768px) {
    position: fixed;
    width: 52.5vw;
  }
`;

const InputS = styled.input`
  max-width: 450px;
`;

const Content = styled.ul`
  list-style: none;
  padding-inline-start: 0px;
  display: grid;
  grid-gap: 1.5em;
  h4 {
    margin: 16px 0 0;
  }
`;

const Dealer = styled.div`
  display: grid;
  grid-gap: 0.5em;
  h6 {
    margin: 0;
  }
`;

// const MarkerS = styled(Marker)`
//   transform: scale(1.1);
// `;

const InRow = styled.div`
  display: grid;
  grid-auto-flow: column;
  justify-content: start;
  align-items: baseline;
  grid-gap: 1em;
`;

const City = styled.div`
  h4 {
    grid-column: 1/-1;
  }
  display: grid;
  grid-gap: 1em;
`;

const Form = styled.form`
  display: grid;
`;

export default DealerFinder;
